import 'package:flutter/material.dart';
import 'package:flutter_manager/widgets/safe.widget.dart';
import 'package:flutter_manager/widgets/separated.widget.dart';

 
class HomePage extends StatelessWidget {

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner:false,
      home: Scaffold(
        body: ListView(
          children: [
            
            Safe(BannerHome())


          ],
        )
      )
    );
  }
}

class BannerHome extends StatelessWidget{
  
  Widget build(context){
    return Container(
      height: 250,
      padding:EdgeInsets.all(15),
      decoration:BoxDecoration(

        color:Colors.blue[900],
        
        borderRadius: BorderRadius.only(
          bottomLeft: Radius.circular(15),
          bottomRight: Radius.circular(15)
        )

      ),
      child:Column(
        
        children: [

          this.barHome()
        ]

    ));


  }

  Row barHome(){
    
    return Row(
      mainAxisAlignment: MainAxisAlignment.spaceBetween,
      children: [
        
        Icon(Icons.data_usage_outlined, color:Colors.white),
        this.avatar()

    ]);


  }
 
  Row avatar(){
    return Row(
      children: [

        Icon(Icons.search, color:Colors.white),
        Separated(20,0),
        CircleAvatar(
          backgroundColor: Colors.transparent,
          child: FadeInImage(placeholder: AssetImage('assets/loading.gif'), image:NetworkImage('data:image/jpeg;base64,'))
        )

      ],
    );
  }

}